import { Component, OnInit, Input } from '@angular/core';
import { Blog } from 'src/app/models/blog.model';
import { Collection } from 'src/app/models/collections.model';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent implements OnInit {

  @Input('input')
  input!: Collection | Blog;

  @Input('type')
  type = 'C' // C, B

  constructor() {}

  castToCourse(input:Collection|Blog){
    return input as Collection;
  }

  castToBlog(input:Collection|Blog){
    return input as Blog;
  }

  ngOnInit(): void {

  }
}
<div class="border border-emerald-100 rounded-3xl p-5">
    <img class="w-full rounded-2xl" src="../../../assets/{{type==='C'?'courses':'blogs'}}/{{input.thumbnail}}" alt="">
    <div class="flex justify-between mt-5 items-center">
        <div class="flex items-center gap-3">
            <img class="w-12 h-12 rounded-full" src="../../../assets/authors/{{input.authorImage}}" alt="">
            <p class="text-slate-500 font-light">{{input.author}}</p>
        </div>
        <div>
            <button class="px-5 py-2 bg-emerald-50 rounded-md text-opacity-80 text-emerald-600">{{input.category}}</button>
        </div>
    </div>
    <h3 class="leading-tight mt-3 text-slate-700 transition-all duration-500 hover:text-emerald-600 font-semibold text-lg">
        {{input.name}}
    </h3>
