    <!-- <app-course-footer [course]="castToCourse(input)" *ngIf="this.type==='C'" class="mt-3 block"></app-course-footer> -->
    <!-- <app-blog-footer [blog]="castToBlog(input)" *ngIf="this.type==='B'" class="mt-3 block"></app-blog-footer> -->
</div>
