import { Component, OnInit,Input } from '@angular/core';


@Component({
  selector: 'app-email',
  templateUrl: './email.component.html',
  styleUrls: ['./email.component.css']
})
export class EmailComponent implements OnInit {

  @Input('align')
  align = 'center';

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="flex gap-2 {{align === 'left' ? 'justify-start' : 'justify-center'}}">
  <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 text-emerald-500" fill="none"
      viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
      <path stroke-linecap="round" stroke-linejoin="round"
          d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207" />
  </svg>

  <a href="mailto: Test@gamil.com" target="_blank" class="hover:text-emerald-500 transition-all duration-500">sabisar@gmail.com</a>
</div>
