  <input formControlName="email" class="mt-4 w-full p-3 border border-emerald-800 border-opacity-20 rounded-lg">
  <!-- <input formControlName="phone" type="number" class="mt-4 w-full p-3 border border-emerald-800 border-opacity-20 rounded-lg"> -->

  <span class="text-red-600" *ngIf="subscribeForm.controls['email'].dirty && !subscribeForm.controls['email'].valid">Invalid Email ID</span>

  <app-button [disabled]="!subscribeForm.valid" class="block mt-4" name="Subscribe Now" size="L" shade="D" animation="C"></app-button>

  <span *ngIf="this.message!=''" class="text-green-600">
      {{this.message}}
  </span>

  <span *ngIf="this.errrorMessage!=''" class="text-red-600">
      {{this.errrorMessage}}
  </span>
</form>
