import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-subscribe-form',
  templateUrl: './subscribe-form.component.html',
  styleUrls: ['./subscribe-form.component.css'],
})
export class SubscribeFormComponent implements OnInit {
}
<form #subscribeForm="ngForm" (ngSubmit)="submit(subscribeForm)">

  <input required [(ngModel)]="email" pattern="^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$" name="email" type="email" class="mt-4 w-full p-3 border border-emerald-800 border-opacity-20 rounded-lg">

  <span class="text-red-600" *ngIf="subscribeForm.controls['email'].dirty && !subscribeForm.controls['email'].valid">Invalid Email ID</span>

  <app-button [disabled]="!subscribeForm.valid" class="block mt-4" name="Subscribe Now" size="L" shade="D" animation="C" (click)="ngClick()"></app-button>

  <span *ngIf="this.message!=''" class="text-green-600">
      {{this.message}}
  </span>

  <span *ngIf="this.errrorMessage!=''" class="text-red-600">
      {{this.errrorMessage}}
  </span>
</form>
