import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  title = ''

  isMenuScrolled = false;
  isSidebarShowing = false;

  @HostListener('window:scroll', ['$event'])
  scrollCheck() {
    if (window.pageYOffset > 100)
      this.isMenuScrolled = true;
    else
      this.isMenuScrolled = false;


    // console.log(this.isMenuScrolled)
  }

  openSideBar() {
    this.isSidebarShowing = true;
  }

  closeSideBar() {
    this.isSidebarShowing = false;
  }

  scrollToTop() {
    document.body.scrollIntoView({ behavior: 'smooth' })
  }
}
<nav class="absolute w-full top-0 left-0 h-screen z-50 {{isSidebarShowing ? 'flex' : 'hidden'}}">
  <div class="bgwhite w-full md:max-w-sm shadow-2xl">
    <div class="flex justify-end p-5"></div>
  </div>
</nav>

<header class="absolute w-full z-40">
  <div class="bg-slate-800 hidden lg:block">
    <div class="container tex-white py-2">
      <ul class="flex justify-between">
        <li></li>
        <li>
          <img src="../assets/shapes/shape-1.webp" alt="shape-1">
        </li>
        <li></li>
        <li></li>
        <li>
          <img src="../assets/shapes/shape-2.webp" alt="shape-2">
        </li>
        <li></li>
      </ul>
    </div>
  </div>
</header>

<router-outlet></router-outlet>
