import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Collection } from 'src/app/models/collections.model';

@Component({
  selector: 'app-collections',
  templateUrl: './collections.component.html',
  styleUrls: ['./collections.component.css']
})
export class CollectionsComponent implements OnInit {

  collections: Collection[] = [];
  filteredCollections: Collection[] = [];

  categories: string[] = [];

  constructor(private httpClient: HttpClient) {}

  ngOnInit(): void {

  }

  getAllCollections() {
    this.httpClient.get('assets/data/courses.json').subscribe({
      next: (collections) => {
        this.collections = collections as Collection[];
        this.filteredCourses = collections as Collection[];
        this.getCategories()
      },
      error: (errors) => {
        console.log(errors)
      }
    })
  }

  getCategories() {
    this.categories = this.collections.map((collection) => { return collection.category })
    this.categories = [...new Set(this.categories)]
  }

  filterCourses(category: string) {
    this.filteredCourses = this.collections.filter(collection => collection.category===category)
  }
}
<section class="pt-24">
  <app-heading class="mb-16 block"
    subHeading=""
    headingText1="All"
    headingText2="Collections"
    headingText3="of Sabitha's Arc"
    specialHeadingID="2"
    align="L"></app-heading>
  <div class="container  mx-auto">
    <app-slider class="block rounded-3xl bg-emerald-50 p-2 py-5 sm:p-10"
      *ngIf="this.collections.length > 0" [items]="this.categories" (select)="filterC"></app-slider>
  </div>
  <div class="mt-12 container mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
    <app-card></app-card>
  </div>
  <div class="flex justify-center mt-16">
    <app-button name="Other Collections" size="L" shade="D" animation="C"  ></app-button>
  </div>
</section>
