import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Collection } from 'src/app/models/collections.model';

@Component({
  selector: 'app-all-collections',
  templateUrl: './all-collections.component.html',
  styleUrls: ['./all-collections.component.css']
})
export class AllCollectionsComponent implements OnInit {

  collections: Collection[] = [];
  filteredCollections: Collection[] = [];

  categories: string[] = [];

  constructor(private httpClient: HttpClient) {}

  ngOnInit(): void {
   this.getAllCollections()
  }

  getAllCollections() {
    this.httpClient.get('assets/data/courses.json').subscribe({
      next: (collections) => {
        this.collections = collections as Collection[];
        this.filteredCollections = collections as Collection[];
        this.getCategories()
      },
      error: (errors) => {
        console.log(errors)
      }
    })
  }

  getCategories() {
    this.categories = this.collections.map((collection) => { return collection.category })
    this.categories = [...new Set(this.categories)]
  }

  filterCollections(category: string) {
    this.filteredCollections = this.collections.filter(collection => collection.category===category)
  }
}
<section class="pt-16">
  <app-heading class="mb-16 block"
    subHeading=""
    headingText1=""
    headingText2=""
    headingText3=""
    specialHeadingID="2"
    align="L"></app-heading>
  <div class="container  mx-auto">
    <app-slider class="block rounded-3xl bg-emerald-50 p-2 py-5 sm:p-10"
      *ngIf="this.categories.length>0" [items]="this.categories" (select)="filterCollections($event)"></app-slider>
  </div>
  <div class="mt-12 container mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        <app-card *ngFor="let course of this.filteredCollections" class="w-full" type="C" [input]="course"></app-card>
  </div>
  <div class="flex justify-center mt-16">
    <app-button name="Other Collections" size="L" shade="D" animation="C"  ></app-button>
  </div>
</section>
