import { AfterViewInit, Component, ContentChild, ElementRef, EventEmitter, HostListener, Input, OnChanges, OnInit, Output, TemplateRef, ViewChild } from '@angular/core';
import { Client } from 'src/app/models/client.model';
import { Course } from 'src/app/models/course.model';
import { Feedback } from 'src/app/models/feedback.model';
import { SlideConfig } from 'src/app/models/slide-config.model';

@Component({
  selector: 'app-slider',
  templateUrl: './slider.component.html',
  styleUrls: ['./slider.component.css']
})
export class SliderComponent implements OnInit, AfterViewInit {

  @Input('items')
  items: string[] | Feedback[] | Client[] = [];

  dots: number[] = [];
  activeSlideID = 1;

  @ContentChild('template')
  template: TemplateRef<any> | undefined;

  @Output('select')
  onSelect: EventEmitter<string> = new EventEmitter<string>()

  @ViewChild('slideContainer')
  slideContainer!: ElementRef;

  @Input('slideConfig')
  slideConfig = new SlideConfig();

  sliderContainerWidth = 0;
  slideWidth = 0;
  elementsToShow = 1;
  sliderWidth = 0;

  sliderMarginLeft = 0;

  isSlidesOver = false;

  @HostListener('window:resize', ['$event'])
  onScreenResize() {
    this.setUpSlider()
  }

  constructor() { }

  ngOnInit(): void {

    console.log(this.dots)
  }

  getItems() {
    return this.items as any[]
  }

  ngAfterViewInit(): void {
    this.setUpSlider()
  }

  setUpSlider() {
    if (window.innerWidth < 500)
      this.elementsToShow = this.slideConfig.breakpoints.sm;
    else if (window.innerWidth < 900)
      this.elementsToShow = this.slideConfig.breakpoints.md;
    else if (window.innerWidth < 1300)
      this.elementsToShow = this.slideConfig.breakpoints.lg;
    else
      this.elementsToShow = this.slideConfig.breakpoints.xl;

    if (this.items.length < this.elementsToShow) {
      this.elementsToShow = this.items.length;
    }

    this.dots = Array(this.items.length - this.elementsToShow + 1);

    let container = this.slideContainer.nativeElement as HTMLElement;

    this.sliderContainerWidth = container.clientWidth;
    this.slideWidth = this.sliderContainerWidth / this.elementsToShow;
    this.sliderWidth = this.slideWidth * this.items.length;

    // console.log(this.sliderContainerWidth)
    // console.log(this.sliderWidth)
    // console.log(this.slideWidth)

    if(this.slideConfig.autoPlay) this.autoPlay()
  }

  prev() {
    console.log(this.sliderMarginLeft)
    if (this.sliderMarginLeft === 0) {
      return
    }
    this.activeSlideID--;
    this.sliderMarginLeft = this.sliderMarginLeft + this.slideWidth;
  }

  next() {
    const notShowingElementsCount = this.items.length - this.elementsToShow;
    const possibleMargin = -(notShowingElementsCount * this.slideWidth);
    if (this.sliderMarginLeft <= possibleMargin) {
      this.isSlidesOver = true;
      return
    }
    this.isSlidesOver = false;
    this.activeSlideID++;
    this.sliderMarginLeft = this.sliderMarginLeft - this.slideWidth;
  }

  move(slideID: number) {
    console.log("Slide ID" + slideID)
    console.log("activeSlideID" + this.activeSlideID)
    let difference = slideID - this.activeSlideID;
    if (difference > 0) {
      // Next
      for (let index = 0; index < difference; index++) {
        this.next()
      }
    } else if (difference < 0) {
      //prev
      for (let index = 0; index < Math.abs(difference); index++) {
        this.prev()
      }
    }
  }

  autoPlay(){
    setTimeout(() => {
      if(this.isSlidesOver === true){
        this.sliderMarginLeft = this.slideWidth;
      }
      this.next()
      this.autoPlay()
    }, 1000);
  }
}
<div>
  <div class="flex w-full justify-between items-center">
      <button *ngIf="this.slideConfig.showLeftRightArrow" (click)="prev()" class="w-8 sm:w-10 sm:h-10 h-8 rounded-full flex bg-white text-slate-800">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
              stroke="currentColor" class="w-5 sm:w-6 h-5 sm:h-6 m-auto">
              <path stroke-linecap="round" stroke-linejoin="round" d="M19.5 12h-15m0 0l6.75 6.75M4.5 12l6.75-6.75" />
          </svg>
      </button>
      <div #slideContainer class="w-full overflow-hidden">
          <ul #slider class="flex transition-all duration-300" [style.width.px]="this.sliderWidth"
              [style.marginLeft.px]="this.sliderMarginLeft">
              <li class="cursor-pointer" (click)="this.onSelect.emit(category)" [style.width.px]="this.slideWidth"
                  *ngFor="let category of this.getItems()">
                  <ng-content *ngTemplateOutlet="template || defaultTemplate;
              context:{$implicit:category}"></ng-content>
              </li>
          </ul>
      </div>
      <button *ngIf="this.slideConfig.showLeftRightArrow" (click)="next()" class="w-8 sm:w-10 sm:h-10 h-8 rounded-full flex bg-white text-slate-800">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
              stroke="currentColor" class="w-5 sm:w-6 h-5 sm:h-6 m-auto">
              <path stroke-linecap="round" stroke-linejoin="round" d="M4.5 12h15m0 0l-6.75-6.75M19.5 12l-6.75 6.75" />
          </svg>
      </button>
  </div>
  <div *ngIf="this.slideConfig.showDots && this.dots.length>1" class="flex justify-center mt-10">
      <ul class="flex gap-2">
          <li (click)="move(i+1)" *ngFor="let dot of dots;let i=index" >
              <span class="w-4 h-4 rounded-full bg-emerald-600 {{i+1===this.activeSlideID?'':'bg-opacity-20'}} flex"></span>
          </li>
      </ul>
  </div>
</div>

<ng-template #defaultTemplate let-item let-width="slideWidth">
  <div
      class="mx-5 rounded-2xl px-5 py-5 text-slate-800 font-semibold text-center border-2 border-emerald-800 border-opacity-10">
      {{item}}
  </div>
</ng-template>
